mapper.select:
select  ,b.name deptName,c.name placeName,d.name levelName from 
man_emp a left join man_dept b on a.deptid=b.id
left join man_place c on a.placeid = c.id
left join man_level d on a.levelid = d.id
WHERE 1=1
AND a.name like CONCAT('%',#{ft.kw},'%')
AND a.placeid = #{ft.placeid}
AND a.birthday > #{ft.monthFirst} AND a.birthday  < #{ft.monthLast}

AND a.entry_time > #{ft.regStart}
AND a.entry_time < #{ft.regEnd}
AND a.sex = #{ft.sex}
AND a.deptid in 
	#{id}

AND a.placeid in (SELECT id FROM man_place WHERE name like CONCAT('%',#{ft.placeKw},'%'))
ORDER BY a.createtime DESC

mapper.update:
	   UPDATE man_emp SET name=#{ model.name }
