mapper.select:
  		select 
  		
  	      from t_sys_advice where 1=1 
		  	and id = #{ vo.id }
		  	and create_user_id = #{ vo.createUserId }
  	        and create_user_name = #{ vo.createUserName }
		  	and create_time = #{ vo.createTime }
  	        and advice = #{ vo.advice }
		  	and reply_user_id = #{ vo.replyUserId }
  	        and reply_user_name = #{ vo.replyUserName }
		  	and reply_time = #{ vo.replyTime }
		  	and status = #{ vo.status }
  	        and reply_content = #{ vo.replyContent }

select 
from t_sys_advice where 1=1
	
	AND  id = #{id,jdbcType=INTEGER}
	
	
	AND  create_user_id = #{createUserId,jdbcType=INTEGER}
	
	
	AND  create_user_name = #{createUserName,jdbcType=VARCHAR}
	
	
	AND  create_time = #{createTime,jdbcType=TIMESTAMP}
	
	
	AND  advice = #{advice,jdbcType=VARCHAR}
	
	
	AND  reply_user_id = #{replyUserId,jdbcType=INTEGER}
	
	
	AND  reply_user_name = #{replyUserName,jdbcType=VARCHAR}
	
	
	AND  reply_time = #{replyTime,jdbcType=TIMESTAMP}
	
	
	AND  status = #{status,jdbcType=INTEGER}
	
	
	AND  reply_content = #{replyContent,jdbcType=VARCHAR}

mapper.delete:
	   delete from t_sys_advice
	   where id in
	      
              #{id,jdbcType=INTEGER}
